client = amino.Client()
def login():
	while True:
		try:
			email = input("Email >>  ")
			password = stdiomask.getpass("Password >> ")
			client.login(email=email, password=password)
			return False
		except amino.lib.util.exceptions.ActionNotAllowed:
			print("Action Not Allowed")
		except amino.lib.util.exceptions.InvalidPassword:
			print("Invalid Password")
		except amino.lib.util.exceptions.InvalidEmail:
			print("Invalid Email")
		except amino.lib.util.exceptions.FailedLogin:
			print("Failed Login")
		except amino.lib.util.exceptions.VerificationRequired as e:
			print(f"Verification required for {email}")
			print(f"Запрашивается верификация для {email}")
			link = e.args[0]['url']
			print(link)
			input("Press Enter >> ")
		except:
			return

